from decimal import Decimalclass Transaction:    def __init__(self, date, size, provider):        self.date = date        self.size = size        self.provider = provider        self.price = Decimal('0.00')        self.discount = Decimal('0.00')        self.valid = True  # Assume valid unless proven otherwise    def set_price(self, price):        self.price = price    def apply_discount(self, discount):        self.discount = discount    def format_output(self):        if not self.valid:            return f"{self.date} {self.size} {self.provider} Ignored"        discount_display = f"{self.discount:.2f}" if self.discount > 0 else "-"        price_display = f"{self.price:.2f}"        return f"{self.date} {self.size} {self.provider} {price_display} {discount_display}"